mapper.select:
SELECT id, NAME, province_id FROM city_info
id = #{id}
AND province_id = #{provinceId}

SELECT id, NAME, city_id FROM area_info
id = #{id}
AND city_id = #{cityId}

SELECT id, NAME, phone, zip_code, (select `name` from province_info where id = address.province_id) province, province_id, (select `name` from city_info where id = address.city_id) city, city_id, (select `name` from area_info where id = address.area_id) area, area_id, address, create_user, is_default, add_time FROM user_address address
create_user = #{id}
ORDER BY is_default desc

SELECT id, `name`, phone, zip_code zipCode, (select `name` from province_info where id = address.province_id) province, province_id, (select `name` from city_info where id = address.city_id) city, city_id, (select `name` from area_info where id = address.area_id) area, area_id, is_default, address FROM user_address address
id = #{userAddressId}
ORDER BY is_default desc
