mapper.select:
SELECT stock.id, stock.count, goods.shop_name, goods.shop_describe, goods.pic, goods.menber_price, goods.stock, goods.id shopGoodsId FROM shop_stock stock JOIN shop_goods goods ON stock.shop_goods_id = goods.id
stock.user_info_id = #{id}
AND goods.platform_goods_category_id = #{platformGoodsCategoryId}
AND goods.shop_name LIKE #{myShopName}
AND stock.count > 0

mapper.update:
UPDATE shop_stock
SET
  count = count - #{buyCount}
WHERE shop_goods_id = #{shopGoodsId}
