SELECT a.id, a.nickname, a.head_portrait, SUM(IF(a.bussiness_type = 7, a.increase_money, 0)) originatorIncreaseMoney, SUM(IF(a.bussiness_type = 8, a.increase_money, 0)) increaseMoney FROM (SELECT fund.increase_money, fund.bussiness_type, userInfo.id, userinfo.nickname, userInfo.head_portrait FROM user_info userInfo LEFT JOIN shop_goods_order shopOrder ON userInfo.id = shopOrder.submit_order_user LEFT JOIN user_fund fund ON fund.order_no = shopOrder.trade_no AND fund.trade_type = 1 AND fund.account_type = 2 AND fund.pay_type = 4 AND (bussiness_type = 7 OR bussiness_type = 8) WHERE userInfo.id IN
#{item}
) a GROUP BY a.id
ORDER BY originatorIncreaseMoney ASC
ORDER BY increaseMoney ASC

